installer-script.script:
function installCheck() {
    if(!(system.compareVersions(system.version.ProductVersion, '10.6.0') >= 0)) {
        my.result.title = 'Unable to install';
        my.result.message = 'Concordium Node requires Mac OS X 10.6 or later.';
        my.result.type = 'Fatal';
        return false;
    }
    if(system.files.fileExistsAtPath('/Library/Concordium Node/1.0.2/')) {
        my.result.title = 'Previous Installation Detected';
        my.result.message = 'A previous installation of Concordium Node exists at /Library/Concordium Node/1.0.2/. This installer will remove the previous installation prior to installing. Please back up any data before proceeding.';
        my.result.type = 'Warning';
        return false;
    }
    return true;
}
